package nl.rabobank.rest.payload;import lombok.Data;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;/** * View Model object for storing a user's credentials. */@Datapublic class LoginRequest {    @NotNull    @Size(min = 6, max = 50)    private String username;    @NotNull    @Size(min = 8, max = 50)    private String password;}