package nl.rabobank.mongo.dao.impl;import nl.rabobank.dao.UserDAO;import nl.rabobank.dto.UserDTO;import nl.rabobank.mongo.mapper.UserMapper;import nl.rabobank.mongo.repository.UserRepository;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class UserDAOImpl implements UserDAO {    private final Logger log = LoggerFactory.getLogger(UserDAOImpl.class);    private final UserRepository userRepository;    public UserDAOImpl(UserRepository userRepository) {        this.userRepository = userRepository;    }    /**     * Find User by username.     *     * @return UserDTO     */    public UserDTO findByUsername(String username) {        log.debug("Find by username {}", username);        return userRepository.findByUsername(username)                .map(value -> UserMapper.mapFromModel(value))                .orElseThrow(() -> new UsernameNotFoundException("Username not found"));    }}