package nl.rabobank.mongo.factory;import nl.rabobank.enums.AccountType;import org.springframework.stereotype.Component;import java.util.Set;@Componentpublic class AccountFactory {    private final Set<AccountDaoFactory> accountDaoFactorySet;    public AccountFactory(Set<AccountDaoFactory> accountDaoFactorySet) {        this.accountDaoFactorySet = accountDaoFactorySet;    }    public AccountDaoFactory getType(AccountType accountType) {        return accountDaoFactorySet.stream().filter(factory -> factory.getType().equals(accountType)).findAny().orElseThrow();    }}