package nl.rabobank.mongo.mapper;import lombok.experimental.UtilityClass;import nl.rabobank.mongo.model.PowerOfAttorneyModel;import nl.rabobank.service.authorizations.PowerOfAttorney;/** * Mapper for Power of Attorney */@UtilityClasspublic class PowerOfAttorneyMapper {    /**     * Map to Model Mapper for Power of Attorney     *     * @return PowerOfAttorneyModel     */    public static PowerOfAttorneyModel mapToModel(PowerOfAttorney powerOfAttorney) {        return PowerOfAttorneyModel.builder()                .grantorName(powerOfAttorney.getGrantorName())                .granteeName(powerOfAttorney.getGranteeName())                .account(powerOfAttorney.getAccount())                .authorization(powerOfAttorney.getAuthorization())                .build();    }    /**     * Map to Power of Attorney     *     * @return PowerOfAttorney     */    public static PowerOfAttorney mapFromModel(PowerOfAttorneyModel powerOfAttorneyModel) {        return PowerOfAttorney.builder()                .granteeName(powerOfAttorneyModel.getGranteeName())                .grantorName(powerOfAttorneyModel.getGrantorName())                .authorization(powerOfAttorneyModel.getAuthorization())                .account(powerOfAttorneyModel.getAccount())                .build();    }}