package nl.rabobank.mongo.mapper;import lombok.experimental.UtilityClass;import nl.rabobank.dto.UserDTO;import nl.rabobank.mongo.model.UserModel;/** * Mapper for User */@UtilityClasspublic class UserMapper {    /**     * Map UserDTO from Model     *     * @return UserDTO     */    public static UserDTO mapFromModel(UserModel userModel) {        return UserDTO.builder()                .id(userModel.getId())                .username(userModel.getUsername())                .password(userModel.getPassword())                .roleType(userModel.getRoleType())                .enabled(userModel.getEnabled())                .build();    }}