package nl.robobank.mongo.model;import nl.rabobank.mongo.model.PaymentAccountModel;import nl.robobank.TestUtil;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import static org.assertj.core.api.Assertions.assertThat;public class PaymentAccountTestModel {    @Test    @DisplayName("payment account verify equals")    void equalsVerifier() throws Exception {        TestUtil.equalsVerifier(PaymentAccountModel.class);        PaymentAccountModel paymentAccount1 = PaymentAccountModel.builder().accountNumber("id").build();        PaymentAccountModel paymentAccount2 = PaymentAccountModel.builder().accountNumber("id2").build();        paymentAccount2.setAccountNumber(paymentAccount1.getAccountNumber());        assertThat(paymentAccount1).isEqualTo(paymentAccount2);        paymentAccount2.setAccountNumber("id2");        assertThat(paymentAccount1).isNotEqualTo(paymentAccount2);        paymentAccount1.setAccountNumber(null);        assertThat(paymentAccount1).isNotEqualTo(paymentAccount2);    }}