package nl.robobank.mongo.model;import nl.rabobank.mongo.model.PowerOfAttorneyModel;import nl.robobank.TestUtil;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import static org.assertj.core.api.Assertions.assertThat;public class PowerOfAttorneyTestModel {    @Test    @DisplayName("power of attorney verify equals")    void equalsVerifier() throws Exception {        TestUtil.equalsVerifier(PowerOfAttorneyModel.class);        PowerOfAttorneyModel powerOfAttorney1 = PowerOfAttorneyModel.builder().id("id").build();        PowerOfAttorneyModel powerOfAttorney2 = PowerOfAttorneyModel.builder().id("id2").build();        powerOfAttorney2.setId(powerOfAttorney1.getId());        assertThat(powerOfAttorney1).isEqualTo(powerOfAttorney2);        powerOfAttorney2.setId("id2");        assertThat(powerOfAttorney1).isNotEqualTo(powerOfAttorney2);        powerOfAttorney1.setId(null);        assertThat(powerOfAttorney1).isNotEqualTo(powerOfAttorney2);    }}