package nl.robobank.mongo.model;import nl.rabobank.mongo.model.SavingsAccountModel;import nl.robobank.TestUtil;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import static org.assertj.core.api.Assertions.assertThat;public class SavingsAccountTestModel {    @Test    @DisplayName("savings account verify equals")    void equalsVerifier() throws Exception {        TestUtil.equalsVerifier(SavingsAccountModel.class);        SavingsAccountModel savingsAccount1 = SavingsAccountModel.builder().accountNumber("id").build();        SavingsAccountModel savingsAccount2 = SavingsAccountModel.builder().accountNumber("id2").build();        savingsAccount2.setAccountNumber(savingsAccount1.getAccountNumber());        assertThat(savingsAccount1).isEqualTo(savingsAccount2);        savingsAccount2.setAccountNumber("id2");        assertThat(savingsAccount1).isNotEqualTo(savingsAccount2);        savingsAccount1.setAccountNumber(null);        assertThat(savingsAccount1).isNotEqualTo(savingsAccount2);    }}