package nl.rabobank.dao;import nl.rabobank.enums.AccountType;import nl.rabobank.service.account.Account;import java.util.Optional;public interface AccountDAO {    /**     * Save a account.     *     * @param account the entity to save.     * @return the persisted account.     */    Account save(Account account);    /**     * Find an account by user id.     *     * @param userId      Authenticated User Id.     * @param accountType Account Type.     * @return the persisted account.     */    Optional<Account> findByUserId(String userId, AccountType accountType);}