package nl.rabobank.dto;import lombok.Data;import lombok.NoArgsConstructor;import nl.rabobank.enums.AccountType;import nl.rabobank.service.account.Account;import javax.validation.constraints.NotEmpty;import javax.validation.constraints.NotNull;/** * Account Request object for storing a new account. */@Data@NoArgsConstructorpublic class AccountDTO {    @NotEmpty    private String accountNumber;    @NotEmpty    private String accountHolderName;    @NotEmpty    private AccountType type;    @NotNull    private Double balance;    public AccountDTO(Account account) {        accountNumber = account.getAccountNumber();        accountHolderName = account.getAccountHolderName();        balance = account.getBalance();        type = account.getType();    }}