package nl.rabobank.dto;import lombok.Builder;import lombok.Data;import nl.rabobank.enums.RoleType;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;/** * User DTO */@Builder@Datapublic class UserDTO {    private String id;    @NotNull    @Size(min = 6, max = 50)    private String username;    @NotNull    @Size(min = 8, max = 50)    private String password;    @NotNull    private RoleType roleType;    private Boolean enabled;}